frappe.ui.form.on('Customer', {
    refresh: function(frm) {
        // Define a handler function for the button click event
        frm.fields_dict.process.$input.on('click', function() {
            // Handle the button click event
            console.log('Button clicked!');
              var tax_id = cur_frm.doc.tax_id;
                frappe.call({
                method: 'ura_efris.api.ura_verification_custom.ura_verification_custom',
               args: { 
                   doc: cur_frm.doc,
                   tax_id: cur_frm.doc.tax_id
                   
               },
               callback: function(response) {
                    cur_frm.refresh();
                }
            });
        });
    }
});

cur_frm.cscript.is_efris = function(doc, cdt, cdn) {
    if (doc.is_efris) {
        // uncheck the second checkbox
        cur_frm.set_value('is_return', 0);
    }
}

cur_frm.cscript.is_return = function(doc, cdt, cdn) {
    if (doc.is_return) {
        // uncheck the first checkbox
        cur_frm.set_value('is_efris', 0);
    }
}

frappe.ui.form.on('Sales Invoice', {
    onload: function(frm) {
        // Bind event to the Save button
        frm.page.set_primary_action(__('Save'), function() {
            // Your custom logic here
            frappe.msgprint('Save button clicked!');
            console.log("am around")
            // To save the form after showing the message
            frm.save();
        });
    }
});
